export default function handler(lambda) {
  return async function (event, context) {
    let body, statusCode;

    try {
     // Print out the full error
  	console.log(e);

      // Run the Lambda
      body = await lambda(event, context);
      statusCode = 200;
    } catch (e) {
      body = { error: e.message };
      statusCode = 500;
    }

    // Return HTTP response
    return {
      statusCode,
      body: JSON.stringify(body),
    };
  };
}
